import axios from "axios";

// Authentication Details
// Resgister API

export const registerData =async (data)=>{
    try {
        return await axios.post(`proce/api/register`, data)
    }
    catch(error){
        console.log('Error while calling api.');
    }
}

//  Login API
export const loginData =async (data)=>{
    try {
        return await axios.post(`${baseAUthUrl}/api/login`, data)
    }
    catch(error){
        console.log('Error while calling api.');
    }
}

// Blog 

const baseBlogUrl = "https://restapinodejs.onrender.com"

// Get Blog Data
export const allBlogData = async ()=>{
    try{
        return await axios.get(`${baseBlogUrl}/api/allBlog`)
    }catch(error){
        console.log("Error while fetching the api.");
    }
}

// Blog Details

export const blogDetailsData = async (id)=>{
    try {
        return await axios.get(`${baseBlogUrl}/api/blogdetails/${id}`)
    }catch (error){
        console.log('Error while fetching the api data.');
    }
}

// All Category
export const AllCategoryData = async ()=>{
    try{
        return await axios.get(`${baseBlogUrl}/api/showallcategory`)
    }catch(error){
        console.log("Error while fetching api data.");
    }
}

// Recent Post 
export const recentPostData = async ()=>{
    try{
        return await axios.get (`${baseBlogUrl}/api/letest-post`)
    }catch(error){
        console.log('Error while fetching api data.');
    }
}

// Comments
export const commentsData = async(id)=>{
    try {
        return await axios.get(`${baseBlogUrl}/api/comment/${id}`)
    }catch(error){
        console.log("Error while fetching the api data.");
    }
}

// Post Comments 

export const postComments = async (id, data)=>{
    try{
        return await axios.post(`${baseBlogUrl}/api/blog/${id}/comment/create`, data)
    }catch(error){
        console.log('Error while posting data on the api.');
    }
}

// Category Details
export const categoryDetailsData = async (id)=>{
    try{
        return await axios.get (`${baseBlogUrl}/api/category/post/${id}`)
    }catch(error){
        console.log('Error while fetching api data.');
    }
}

// Serach 
export const searchData = async()=>{
    try{
        return await axios.get(`${baseBlogUrl}/api/search`)
    }catch(error){
        console.log('Error while fetching the api data');
    }
}
