import React, { useState } from "react";
import Layout from "../components/Layout";
import { Container, FormControl, FormGroup, Grid, InputLabel } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/Auth";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [auth, setAuth] = useAuth();

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await loginData({
        email,
        password,
    });
    if (response && response.data.success) {
      setAuth({
        ...auth,
        user: response.data.user,
        token: response.data.token,
      });
      console.log("data", response.data);
      navigate("/");
      toast.success(response?.message)
      localStorage.setItem("auth", JSON.stringify(response.data));
    }
  };
  return (
    <>
      <Layout title={"Login Page"}>
      <Container maxWidth="sm" 
      sx={{
        width: "50%",
        margin: "auto",
        marginTop: "35px",
        boxShadow: "0px 0px 30px rgba(0,0,0,0.5)"
      }}>
        <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
          <Grid item xs={6}>
            <img src="/assets/login_img.png" alt="" />
          </Grid>
          <Grid item xs={6}>
          <form onSubmit={handleSubmit}>
            < FormGroup
              sx={{
                width: "50%",
                margin: "auto",
                padding: 10,
                boxShadow: "0px 0px 30px rgba(0,0,0,1)",
              }}
            >
              <FormControl sx={{ paddingBottom: 2 }} >
                <InputLabel>Email</InputLabel>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </FormControl>
              <FormControl>
                <InputLabel>Password</InputLabel>
                <Input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </FormControl>
              <Button
                variant="contained"
                color="error"
                sx={{ mt: 5, mb: 2 }}
                type="submit"
              >
                Login
              </Button>
              <div>
              *** Don't have any account? Create One!! <Link to='/register'> Create Account</Link>
              </div>
            </FormGroup>
          </form>
          </Grid>
        </Grid>
        </Container>
      </Layout>
    </>
  );
};

export default Login;
