import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { blogDetailsData, commentsData } from "../service/Api";
import Layout from "../components/Layout";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";

const BlogDetails = () => {
  const [blog, setBlog] = useState([]);
  const [comment, setComment] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  const imgUrl = "https://restapinodejs.onrender.com";

  const { id } = useParams();
  console.log("id", id);

  const getBlogDetailsData = async () => {
    const response = await blogDetailsData(id);
    setBlog(response?.data?.data);
    console.log("data", response);
  };

  const getComments = async () => {
    const res_comments = await commentsData(id);
    setComment(res_comments?.data?.post?.comment?.comments);
    console.log("comments", res_comments);
  };

  console.log("com", comment);

  useEffect(() => {
    getBlogDetailsData();
    getComments();
  }, []);
  return (
    <>
      <Layout title={"Blog-Details"}>
        <Container>
          <Card sx={{ boxShadow: "0px 0px 30px rgba(0,0,0,0.5)" }}>
            <CardMedia
              sx={{ height: 600 }}
              image={`${imgUrl}/api/blog/image/${id}`}
              title="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {blog.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {blog.postText}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate(-1)}>
                Go Back
              </Button>
            </CardActions>
          </Card>
          {/* Comments */}
          <div>
            <h2>Comments ({comment.length})</h2>
            <Container>
              {comment?.map((comments, key) => {
                return (
                  <>
                    <Typography sx={{ fontWeight: "bold" }} key={key}>
                      {comments.name}
                    </Typography>
                    <Typography
                      sx={{ fontSize: 20, fontFamily: "times new roman" }}
                      color="text.secondary"
                      gutterBottom
                    >
                      {comments.comment}
                    </Typography>
                  </>
                );
              })}
            </Container>
          </div>
        </Container>
      </Layout>
    </>
  );
};

export default BlogDetails;
