import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import { getCourses } from "../service/Api";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Typography from "@mui/material/Typography";
import Button from '@mui/material/Button';

const Courses = () => {
  const [course, setCourse] = useState([]);
  const imgUrl = "https://restapinodejs.onrender.com";

  const CourseData = async () => {
    const response = await getCourses();
    setCourse(response?.data?.Courses);
    console.log("course", course);
  };

  useEffect(() => {
    CourseData();
  }, []);
  return (
    <>
      <Layout title={"PhoenixTech-Courses"}>
        <Container>
          <Grid
            container
            rowSpacing={1}
            columnSpacing={{ xs: 1, sm: 2, md: 3 }}
            sx={{ mt: 3 }}
          >
            {course?.map((element, index) => {
              return (
                <>
                  <Grid item xs={4} key={index}>
                    <Card sx={{ maxWidth: 345, m: 2, borderRadius: 4 }}>
                      <CardHeader
                        sx={{
                          backgroundColor: "red",
                          height: 15,
                          textAlign: "center",
                          color: "white",
                        }}
                        title={element.name}
                      />
                      <CardMedia
                        component="img"
                        height="200"
                        width="50"
                        image={`${imgUrl}/api/course/photo/${element._id}`}
                        alt="Paella dish"
                      />
                      <CardContent>
                        <Typography
                          variant="h4"
                          color="text.secondary"
                          sx={{ textAlign: "center", color: "red" }}
                        >
                          â‚¹ {element.fees} /-
                        </Typography>
                        <Typography
                          color="text.secondary"
                          sx={{ textAlign: "center" }}
                        >
                          * Requirement: {element.requirement}
                        </Typography>
                        <Typography
                          color="text.secondary"
                          sx={{ textAlign: "center" }}
                        >
                          * Duration: {element.duration}
                        </Typography>
                        <Button variant="contained" color="error" sx={{m: 2}}>
                          Apply
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                </>
              );
            })}
          </Grid>
        </Container>
      </Layout>
    </>
  );
};

export default Courses;
