import React from "react";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import { useAuth } from "../context/Auth";

const Footer = () => {
  const privatePage = ["Home", "About", "Courses", "Blog", "Contact"];
  const publicPage = ["Home", "About", "Courses", "Blog", "Contact", "Login"];
  const [auth] = useAuth();
  return (
    <>
      <Container
        maxWidth="xl"
        sx={{ backgroundColor: "#0F1924", height: "auto", mt: 2 }}
      >
        <Grid
          container
          rowSpacing={1}
          columnSpacing={{ xs: 1, sm: 2, md: 3 }}
          direction={{ xs: "column", md: "row" }}
          justifyContent={{ xs: "center", md: "flex-end" }}
          alignItems={{ xs: "center" }}
        >
          <Grid item xs={3}>
            <Typography
              variant="h5"
              sx={{ color: "white", fontWeight: "bold", textAlign: "center" }}
            >
              PhoenixTech
            </Typography>
            <Typography sx={{ color: "white", textAlign: "center" }}>
              Eco Intelligent Park, Unit No- 7E, 7th Floor, Block- EM, Plot-9,
              Salt Lake Sector-5, Kolkata 700091, India
            </Typography>
          </Grid>
          <Grid item xs={3}>
            <Typography
              variant="h5"
              sx={{ color: "white", fontWeight: "bold", textAlign: "center" }}
            >
              Useful Links
            </Typography>
            <List >
              {!auth.user
                ? publicPage?.map((page) => {
                    return (
                      <>
                        <ListItem sx={{}}>
                          <ListItemIcon>
                            <ArrowForwardIosIcon
                              sx={{ color: "red", textAlign: "center" }}
                            />
                          </ListItemIcon>
                          <ListItemText
                            primary={page}
                            sx={{ color: "white" }}
                          />
                        </ListItem>
                      </>
                    );
                  })
                : privatePage?.map((page) => {
                    return (
                      <>
                        <ListItem>
                          <ListItemIcon>
                            <ArrowForwardIosIcon
                              sx={{ color: "red", textAlign: "center" }}
                            />
                          </ListItemIcon>
                          <ListItemText
                            primary={page}
                            sx={{ color: "white" }}
                          />
                        </ListItem>
                      </>
                    );
                  })}
            </List>
          </Grid>
          <Grid item xs={3}>
            <Typography
              variant="h5"
              sx={{ color: "white", fontWeight: "bold", textAlign: "center" }}
            >
              Our Services
            </Typography>
          </Grid>
          <Grid item xs={3}>
            <Typography
              variant="h5"
              sx={{ color: "white", fontWeight: "bold", textAlign: "center" }}
            >
              Join Our Newsletter
            </Typography>
          </Grid>
        </Grid>
      </Container>
    </>
  );
};

export default Footer;
