import React, { useState } from "react";
import Layout from "../components/Layout";
import { Button, Container, FormControl, FormGroup, Grid, Input, InputLabel } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/Auth";
import { toast } from "react-toastify";
import { loginData } from "../service/Api";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [auth, setAuth] = useAuth();

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await loginData({
        email,
        password,
    });
    if (response) {
      setAuth({
        ...auth,
        user: response.data.user,
        token: response.data.token,
      });
      // console.log("data", response.data);
      navigate("/home");
      toast.success(response?.data?.message)
      localStorage.setItem("auth", JSON.stringify(response.data));
    }else{
      toast.error(response?.data?.message)
    }
  };
  return (
    <>
      <Layout title={"PhoenixTech-Login"}>
      <Container  
      sx={{
        marginTop: "5%",
        boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
        borderRadius: "20px"
      }}>
        <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 6 }}>
          <Grid item xs={4}>
            <img src="/assets/login_img.png" alt="" style={{margin: "20% 0% 20% 25%"}}/>
          </Grid>
          <Grid item xs={8}>
          <h3 style={{textAlign: "center", fontWeight: "bold", color: "#C62828", fontSize: "30px", marginTop: 10}}>LOGIN HERE</h3>
          <form onSubmit={handleSubmit}>
            < FormGroup
              sx={{
                width: "auto",
                marginLeft: 10,
                paddingTop: 5,
              }}
            >
              <FormControl sx={{ paddingBottom: 2 }} >
                <InputLabel>Email</InputLabel>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </FormControl>
              <FormControl>
                <InputLabel>Password</InputLabel>
                <Input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </FormControl>
              <Button
                variant="contained"
                color="error"
                sx={{ mt: 5, mb: 2 }}
                type="submit"
              >
                Login
              </Button>
              <div style={{textAlign: "center"}}>
              Don't have any account? <Link to='/register' style={{textDecoration: "none", fontWeight: "bold", color: "#C62828", }} > REGISTER HERE</Link>
              </div>
            </FormGroup>
          </form>
          </Grid>
        </Grid>
        </Container>
      </Layout>
    </>
  );
};

export default Login;
