import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import { AllCategoryData, allBlogData } from "../service/Api";
import {
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";
import { Link } from "react-router-dom";
import Skeleton from '@mui/material/Skeleton';


const Blog = () => {
  const imgUrl = "https://restapinodejs.onrender.com";

  const [blog, setBlog] = useState({});
  const [category, setCategory] = useState()
  const [loading, setLoading] = useState(true);

  const getAllBlogData = async () => {
    const response = await allBlogData();
    setBlog(response?.data?.data);
    setLoading(false);
    // console.log('data', response);
  };

  const getAllCategoryData = async ()=>{
    const response_cat = await AllCategoryData()
    setCategory(response_cat?.data?.data)
    console.log('catData', response_cat)
  }

  // console.log("blog", blog);

  useEffect(() => {
    getAllBlogData();
    getAllCategoryData();
  }, []);

  // Load More Data Option
  const dataPerRow = 3;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };

  return (
    <>
      <Layout title={"PhoenixTech-Blog"}>
        <Container sx={{ marginTop: 5 }}>
          <Grid
            container
            rowSpacing={1}
            columnSpacing={{ xs: 1, sm: 2, md: 12 }}
          >
            <Grid item xs={9}>
              {
              loading ? 
              <>
                <Card
                        sx={{
                          width: 900,
                          boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                        }}
                      >
                        <CardHeader
                          title={
                            <Skeleton variant="text" sx={{ fontSize: '3rem' }} />   
                          }
                          subheader={<Skeleton variant="text" sx={{ fontSize: '1rem' }} />  }
                        />
                        <Skeleton variant="rectangular" width={900} height={400} />
                        <CardContent>
                          <Typography variant="body2" color="text.secondary">
                            {<Skeleton variant="text" sx={{ fontSize: '1rem' }} />  }
                          </Typography>
                            <Skeleton variant="text" sx={{ fontSize: '1rem' }} />
                        </CardContent>
                      </Card>
              </> : 
              blog?.length > 0 &&
                blog?.slice(0, loadMoreData).map((blog, key) => {
                  return (
                    <>
                      <Card
                        sx={{
                          width: 900,
                          boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                        }}
                      >
                        <CardHeader
                          title={
                            loading ? <Skeleton variant="text" sx={{ fontSize: '1rem' }} /> :
                            blog.title
                          }
                          subheader={blog.createdAt}
                        />
                        <CardMedia
                          component="img"
                          height="400"
                          image={`${imgUrl}/api/blog/image/${blog._id}`}
                          alt="No Image Found"
                        />
                        <CardContent>
                          <Typography variant="body2" color="text.secondary">
                            {blog.postText.slice(0, 300)}
                          </Typography>
                          <Link to={`/blog_details/${blog._id}`}>
                            Read More
                          </Link>
                        </CardContent>
                      </Card>
                    </>
                  );
                })}
            </Grid>
            <Grid item xs={3}>
              Category
            </Grid>
          </Grid>
          {loadMoreData < blog?.length && (
            <Button
              variant="contained"
              color="error"
              sx={{ margin: 2 }}
              onClick={handleMoreData}
            >
              Load More
            </Button>
          )}
        </Container>
      </Layout>
    </>
  );
};

export default Blog;
