import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import { allBlogData } from "../service/Api";
import { RingLoader } from "react-spinners";
import { Card, CardContent, CardHeader, CardMedia, Typography } from "@mui/material";
import { Link } from "react-router-dom";

const Blog = () => {

  const imgUrl = "https://restapinodejs.onrender.com"

  const [blog, setBlog] = useState({});
  const [loading, setLoading] = useState(true);

  const getAllBlogData = async () => {
    const response = await allBlogData();
    setBlog(response?.data?.data);
    setLoading(false)
    // console.log('data', response);
  };

  console.log("blog", blog);

  useEffect(() => {
    getAllBlogData();
  }, []);

  return (
    <>
      <Layout title={"PhoenixTech-Blog"}>
        <Container sx={{marginTop: 5}}>
          <Grid
            container
            rowSpacing={1}
            columnSpacing={{ xs: 1, sm: 2, md: 12 }}
          >
            <Grid item xs={9}>
              {loading ? (
                <RingLoader color="#de3000" size={106}/>
              ) : (
                <>
                  {
                    blog?.map((blog, key)=>{
                      return (
                        <>
                          <Card sx={{ width: 900, boxShadow: "0px 0px 30px rgba(0,0,0,0.5)" }}>
                    <CardHeader
                      title={blog.title}
                      subheader={blog.createdAt}
                    />
                    <CardMedia
                      component="img"
                      height="400"
                      image={`${imgUrl}/api/blog/image/${blog._id}`}
                      alt="No Image Found"
                    />
                    <CardContent>
                      <Typography variant="body2" color="text.secondary">
                        {blog.postText.slice(0, 300)}
                      </Typography>
                      <Link to={`/blog_details/${blog._id}`}>Read More</Link>
                    </CardContent>
                  </Card>
                        </>
                      )
                    })
                  }
                </>
              )}
            </Grid>
            <Grid item xs={3}>
              Category
            </Grid>
          </Grid>
        </Container>
      </Layout>
    </>
  );
};

export default Blog;
