import React, { useState } from 'react'
import Layout from '../components/Layout'
import { useAuth } from '../context/Auth';
import { registerData } from '../service/Api';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mobile, setMobile] = useState("");
  const [auth, setAuth] = useAuth()

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
      const response = await registerData({
        name,
        email,
        mobile,
        password,
      })
      if (response && response.data.success) {
        navigate("/login");
        console.log("reg", response.data);
      } else {
        console.log(response.data);
      }
    }
  return (
    <>
      <Layout title={'PhoenixTech-Register'}>
      <Container  
      sx={{
        marginTop: "5%",
        boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
        borderRadius: "20px"
      }}>
        <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 6 }}>
          <Grid item xs={4}>
            <img src="/assets/login_img.png" alt="" style={{margin: "20% 0% 20% 25%"}}/>
          </Grid>
          <Grid item xs={8}>
          <h3 style={{textAlign: "center", fontWeight: "bold", color: "#C62828", fontSize: "30px", marginTop: 30}}>LOGIN HERE</h3>
          <form onSubmit={handleSubmit}>
            < FormGroup
              sx={{
                width: "auto",
                marginLeft: 10,
                paddingTop: 2,
                paddingLeft: 5,
                paddingRight: 15
              }}
            >
              <FormControl sx={{ paddingBottom: 2 }} >
                <InputLabel>Email</InputLabel>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </FormControl>
              <FormControl>
                <InputLabel>Password</InputLabel>
                <Input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </FormControl>
              <Button
                variant="contained"
                color="error"
                sx={{ mt: 5, mb: 2 }}
                type="submit"
              >
                {loading==="logging" ? (
                  <>
                  <CircularProgress sx={{color: "white", marginRight: 1}} size={"1rem"}/>
                    Logging ...
                  </> )  : "Login" }
              </Button>
              <div style={{textAlign: "center"}}>
              Don't have any account? <Link to='/register' style={{textDecoration: "none", fontWeight: "bold", color: "#C62828", }} > REGISTER HERE</Link>
              </div>
            </FormGroup>
          </form>
          </Grid>
        </Grid>
        </Container>
        </Layout> 
    </>
  )
}

export default Register
