import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import {
  AllCategoryData,
  allBlogData,
  recentPostData,
  searchData,
} from "../service/Api";
import {
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Typography,
  Button,
  Paper,
  TextField,
} from "@mui/material";
import { Link, useNavigate } from "react-router-dom";
import Skeleton from "@mui/material/Skeleton";
import SearchIcon from "@mui/icons-material/Search";
import { styled, alpha } from "@mui/material/styles";
import InputBase from "@mui/material/InputBase";
import { useAuth } from "../context/Auth";
import swal from "sweetalert";
import moment from "moment/moment";
import PersonIcon from "@mui/icons-material/Person";
import CalendarMonthIcon from "@mui/icons-material/CalendarMonth";
import CommentIcon from "@mui/icons-material/Comment";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";

const Blog = () => {
  const imgUrl = "https://restapinodejs.onrender.com";

  const [blog, setBlog] = useState([]);
  const [category, setCategory] = useState([]);
  const [recentPost, setRecentPost] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loading1, setLoading1] = useState(true);
  const [loading2, setLoading2] = useState(true);
  const [auth] = useAuth();
  const navigate = useNavigate();

  const getAllBlogData = async () => {
    const response = await allBlogData();
    setBlog(response?.data?.data);
    setLoading(false);
    // console.log('data', response);
  };

  const getAllCategoryData = async () => {
    const response_cat = await AllCategoryData();
    setCategory(response_cat?.data?.data);
    setLoading1(false);
    // console.log("catData", category);
  };

  const getRecentPostData = async () => {
    const response_recentPost = await recentPostData();
    setRecentPost(response_recentPost?.data?.data);
    setLoading2(false);
    // console.log("recent", response_recentPost);
  };

  // console.log("RPost", recentPost);

  useEffect(() => {
    getAllBlogData();
    getAllCategoryData();
    getRecentPostData();
  }, []);

  // Load More Data Option
  const dataPerRow = 3;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };

  // Search
  const [searchItem, setSearchItem] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  const handleSearch = () => {
    const result = blog.filter((item) =>
      item.title.toLowerCase().includes(searchItem.toLowerCase())
    );
    setSearchResult(result);
  };
  console.log("search", searchResult);

  const Search = styled("div")(({ theme }) => ({
    position: "relative",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    "&:hover": {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      marginLeft: theme.spacing(1),
      width: "81%",
    },
  }));

  const SearchIconWrapper = styled("div")(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: "100%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  }));

  return (
    <>
      <Layout title={"PhoenixTech-Blog"}>
        <Container sx={{ mt: 4 }}>
          <Grid
            container
            spacing={0}
            direction={{ xs: "column", md: "row" }}
            justifyContent={{ xs: "center", md: "flex-end" }}
          >
            <Grid item xs={9} pr={3}>
              {loading ? (
                <>
                  <Card
                    sx={{
                      width: "auto",
                      boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                    }}
                  >
                    <CardHeader
                      title={
                        <Skeleton variant="text" sx={{ fontSize: "3rem" }} />
                      }
                      subheader={
                        <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                      }
                    />
                    <Skeleton
                      variant="rectangular"
                      width={"100%"}
                      height={400}
                    />
                    <CardContent>
                      <Typography variant="body2" color="text.secondary">
                        {<Skeleton variant="text" sx={{ fontSize: "1rem" }} />}
                      </Typography>
                      <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                    </CardContent>
                  </Card>
                </>
              ) : (
                blog?.length > 0 &&
                blog?.slice(0, loadMoreData).map((blogs, key) => {
                  return (
                    <>
                      <Card
                        key={key}
                        sx={{
                          width: "auto",
                          boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                          mb: 4,
                        }}
                      >
                        <CardHeader
                          title={blogs.title}
                          subheader={
                            <>
                              <span>
                                <PersonIcon style={{ fontSize: "medium" }} />{" "}
                                John Doe
                              </span>
                              <time style={{ paddingLeft: "2vw" }}>
                                <CalendarMonthIcon
                                  style={{
                                    fontSize: "medium",
                                    paddingTop: "5px",
                                  }}
                                />
                                {moment(blogs.createdAt).format(" Do MM, YYYY")}
                              </time>
                              <span style={{ paddingLeft: "2vw" }}>
                                <CommentIcon
                                  style={{
                                    fontSize: "medium",
                                    paddingTop: "5px",
                                    paddingRight: "0.3vw",
                                  }}
                                />
                                {blogs.comment_count}
                              </span>
                              <span>
                                <ThumbUpIcon
                                  style={{
                                    fontSize: "medium",
                                    paddingTop: "5px",
                                    paddingLeft: "1vw",
                                  }}
                                />{" "}
                                ({blogs?.like})
                              </span>
                            </>
                          }
                        />
                        <CardMedia
                          component="img"
                          height="400"
                          image={`${imgUrl}/api/blog/image/${blogs._id}`}
                          alt="No Image Found"
                        />
                        <CardContent>
                          <Typography
                            variant="body2"
                            color="text.secondary"
                            dangerouslySetInnerHTML={{
                              __html: blogs?.postText.slice(0, 300),
                            }}
                          >
                            {/* {blogs.postText.slice(0, 300)} */}
                          </Typography>
                          {!auth.user ? (
                            <Link
                              onClick={() =>
                                swal({
                                  title: "Are you logged in?",
                                  text: "Please login first to access further.",
                                  icon: "error",
                                  dangerMode: true,
                                })
                              }
                              style={{ textDecoration: "none", color: "blue" }}
                            >
                              Read More
                            </Link>
                          ) : (
                            <Link
                              to={`/blogdetails/${blogs._id}`}
                              style={{ textDecoration: "none", color: "blue" }}
                            >
                              Read More
                            </Link>
                          )}
                        </CardContent>
                        {/* <p dangerouslySetInnerHTML={{ __html: api1?.postText }}></p> */}
                      </Card>
                    </>
                  );
                })
              )}
            </Grid>
            <Grid
              item
              xs={3}
              sx={{ boxShadow: "0px 0px 30px rgba(0,0,0,0.5)", mb: 4, p: 2 }}
            >
              <h3>Search</h3>
                <TextField
                    name="search"
                    type="text"
                    placeholder="Search"
                    value={searchItem}
                    onChange={(e) => setSearchItem(e.target.value)}
                    margin="0"                   
                  />
              <h3>Categories</h3>
              {loading1 ? (
                <>
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                </>
              ) : (
                category?.map((category, key) => {
                  return (
                    <>
                      <Grid item xs={12} key={key} sx={{ padding: 1 }}>
                        <Link
                          to={`/categorydetails/${category._id}`}
                          style={{ textDecoration: "none", color: "black" }}
                        >
                          {category.category}{" "}
                          <span style={{ color: "grey", marginLeft: "10px" }}>
                            {category.category.length}
                          </span>
                        </Link>
                      </Grid>
                    </>
                  );
                })
              )}
              <h3>Recent Posts</h3>
              {loading2 ? (
                <>
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                  <Skeleton
                    variant="text"
                    sx={{ fontSize: "1rem", marginRight: "25px" }}
                  />
                </>
              ) : (
                <Grid
                  container
                  rowSpacing={1}
                  columnSpacing={{ xs: 1, sm: 2, md: 3 }}
                >
                  {recentPost?.map((post, key) => {
                    return (
                      <>
                        <Grid item xs={4} sx={{ padding: 1 }}>
                          <img
                            src={`${imgUrl}/api/blog/image/${post._id}`}
                            alt=""
                            height={"100%"}
                            width={"100%"}
                          />
                        </Grid>
                        <Grid item xs={8} sx={{ padding: 1 }}>
                          <Link
                            to={`/blogdetails/${post._id}`}
                            style={{
                              textDecoration: "none",
                              color: "black",
                              fontWeight: 500,
                            }}
                            className="recentPostLink"
                          >
                            {post.title}
                          </Link>
                          <Typography sx={{ color: "grey", fontSize: "small" }}>
                            <time>
                              {moment(post.createdAt).format(
                                " Do MM, YYYY, h:mm a"
                              )}
                            </time>
                          </Typography>
                        </Grid>
                      </>
                    );
                  })}
                </Grid>
              )}
            </Grid>
          </Grid>
          {loadMoreData < blog?.length && (
            <Button
              variant="contained"
              color="error"
              sx={{ margin: 1 }}
              onClick={handleMoreData}
            >
              Load More
            </Button>
          )}
        </Container>
      </Layout>
    </>
  );
};

export default Blog;
