  }

  useEffect(() => {
    getBanenrData();
  }, []);

  const bannerStyle = {
    position: 'absolute',
    top: '10px',
    left: '10px',
    background: 'rgba(0, 0, 0, 0.5)',
    color: 'white',
    padding: '10px',
    fontSize: '18px',
    borderTop: "5px solid red",
    width: "50%",
    mt: "25vh",
    ml: "25vw"
  };

  return (
    <div>
      <Layout title={"PhoenixTech-Home"}>
        {/* Carousel */}
        <Carousel showThumbs={false}>
          {banners.map((banner, index) => (
            <div>
              <img key={index} src={`${imgURL}/api/banner/photo/${banner._id}`} alt="No Photo" height={500}/>
              <Box sx={bannerStyle}>
                <Typography variant="h4" p={2} fontWeight={"bold"}>{banner.title}</Typography>
                <Typography>{banner.description}</Typography>
                <Button variant="outlined" sx={{border: "2px solid red", color: "white", m: 2}}>Read More</Button>
              </Box>
            </div>
          ))}
        </Carousel>

        {/* Service */}
        <Container sx={{ bgcolor: '#cfe8fc', height: 'auto' }}>
          {}
      </Container>
      </Layout>
    </div>
  );
};

export default Home;
