import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import { BannerData } from "../service/Api";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Box from "@mui/material/Box";
import { Typography } from "@mui/material";

const Home = () => {
  const [banners, setBanners] = useState([]);
  const imgURL = "https://restapinodejs.onrender.com"

  const getBanenrData = async () => {
    const response = await BannerData();
    setBanners(response?.data?.bannerdata);
    console.log("banner", banners);
  };

  useEffect(() => {
    getBanenrData();
  }, []);

  const bannerStyle = {
    position: 'absolute',
    top: '10px',
    left: '10px',
    background: 'rgba(0, 0, 0, 0.5)',
    color: 'white',
    padding: '10px',
    fontSize: '18px',
    borderTop: "5px solid #00FF40",
    width: "40%"
  };

  return (
    <div>
      <Layout title={"PhoenixTech-Home"}>
        <Carousel showThumbs={false}>
          {banners.map((banner, index) => (
            <div>
              <img key={index} src={`${imgURL}/api/banner/photo/${banner._id}`} alt="No Photo" height={500}/>
              <Box sx={bannerStyle}>
                <Typography>{banner.title}</Typography>
                <Typography>{banner.description}</Typography>
              </Box>
            </div>
          ))}
        </Carousel>
      </Layout>
    </div>
  );
};

export default Home;
