import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { categoryDetailsData } from "../service/Api";
import Container from "@mui/material/Container";
import Skeleton from "@mui/material/Skeleton";
import {
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";
import Layout from "../components/Layout";
import { useAuth } from "../context/Auth";
import swal from "sweetalert";

const CategoryDetails = () => {
  const imgUrl = "https://restapinodejs.onrender.com";
  const [category, setCategory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [auth] = useAuth();

  const { id } = useParams();

  const navigate = useNavigate();

  const getCategoryDetails = async () => {
    const response = await categoryDetailsData(id);
    setCategory(response?.data?.data);
    setLoading(false);
    console.log("cat", response);
  };

  useEffect(() => {
    getCategoryDetails();
  }, []);

  const sweetPopup = ()=>{
    swal({
      title: "No Data Found",
      text: " ",
      icon: "warning",
      dangerMode: true,
      buttons: false,
      onclick: navigate('/blog'),
      timer:3000
    })
  }

  // Load More Data Option
  const dataPerRow = 2;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };

  return (
    <>
      <Layout title={"Category-Details"}>
        <Container sx={{mt: 5}}>
          {loading ? (
            <>
              <Card
                sx={{
                  boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                }}
              >
                <CardHeader
                  title={<Skeleton variant="text" sx={{ fontSize: "3rem" }} />}
                  subheader={
                    <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                  }
                />
                <Skeleton variant="rectangular" height={400} />
                <CardContent>
                  <Typography variant="body2" color="text.secondary">
                    {<Skeleton variant="text" sx={{ fontSize: "1rem" }} />}
                  </Typography>
                  <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                </CardContent>
              </Card>
            </>
          ) : !category.length > 0 ? (
            sweetPopup()
          ) : (
            <>
              {category?.length > 0 &&
                category?.slice(0, loadMoreData).map((category, key) => {
                  return (
                    <>
                      <Card
                        key={key}
                        sx={{
                          boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                          mb: 4,
                        }}
                      >
                        <CardHeader
                          title={category.title}
                          subheader={
                            <>
                            <span><PersonIcon  style={{fontSize: "medium",}}/> John Doe</span>
                            <time style={{paddingLeft: "2vw"}}>
                              <CalendarMonthIcon style={{fontSize: "medium", paddingTop: "5px"}}/>
                              {moment(category.createdAt).format(" Do MM, YYYY")}
                            </time>
                            <span style={{paddingLeft: "2vw"}}><CommentIcon style={{fontSize: "medium", paddingTop: "5px", paddingRight: "0.3vw"}}/>{category.comment_count}</span>
                            <span><ThumbUpIcon style={{fontSize: "medium", paddingTop: "5px", paddingLeft: "1vw"}}/> ({category?.like})</span>
                            </>
                          }
                        />
                        <CardMedia
                          component="img"
                          height="400"
                          image={`${imgUrl}/api/blog/image/${category._id}`}
                          alt="No Image Found"
                        />
                        <CardContent>
                          <Typography variant="body2" color="text.secondary" dangerouslySetInnerHTML={{
                              __html: category?.postText.slice(0, 300),
                            }}>
                            {/* {category.postText.slice(0, 300)} */}
                          </Typography>
                          {!auth.user ? (
                            <Link onClick={()=>swal({
                              title: "Are you logged in?",
                              text: "Please login first to access further.",
                              icon: "error",
                              dangerMode: true,
                            })}>Read More</Link>
                          ) : (
                            <Link to={`/blogdetails/${category._id}`} style={{textDecoration: "none", color: "blue"}}>
                              Read More
                            </Link>
                          )}
                        </CardContent>
                      </Card>
                    </>
                  );
                })}
              <Button
                variant="outlined"
                color="error"
                size="small"
                onClick={() => navigate(-1)}
                sx={{ margin: 2 }}
              >
                Go Back
              </Button>
            </>
          )}
          {loadMoreData < category?.length && (
            <Button
              variant="contained"
              color="error"
              sx={{ margin: 2 }}
              onClick={handleMoreData}
            >
              Load More
            </Button>
          )}
        </Container>
      </Layout>
    </>
  );
};

export default CategoryDetails;
