import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { categoryDetailsData } from "../service/Api";
import Container from "@mui/material/Container";
import Skeleton from "@mui/material/Skeleton";
import {
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";

const CategoryDetails = () => {
  const imgUrl = "https://restapinodejs.onrender.com";
  const [category, setCategory] = useState([]);
  const [loading, setLoading] = useState(true);


  const { id } = useParams();

  const getCategoryDetails = async () => {
    const response = await categoryDetailsData(id);
    setCategory(response?.data);
    setLoading(false);
    console.log("cat", category);
  };

  useEffect(() => {
    getCategoryDetails();
  }, []);

  // Load More Data Option
  const dataPerRow = 3;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };
  return (
    <>
      <Container>
        {loading ? (
          <>
            <Card
              sx={{
                width: 800,
                boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
              }}
            >
              <CardHeader
                title={<Skeleton variant="text" sx={{ fontSize: "3rem" }} />}
                subheader={
                  <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                }
              />
              <Skeleton variant="rectangular" width={900} height={400} />
              <CardContent>
                <Typography variant="body2" color="text.secondary">
                  {<Skeleton variant="text" sx={{ fontSize: "1rem" }} />}
                </Typography>
                <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
              </CardContent>
            </Card>
          </>
        ) : (
          category?.length > 0 &&
          category?.slice(0, loadMoreData).map((blog, key) => {
            return (
              <>
                <Card
                  key={key}
                  sx={{
                    width: 800,
                    boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                  }}
                >
                  <CardHeader title={category.title} subheader={category.createdAt} />
                  <CardMedia
                    component="img"
                    height="400"
                    image={`${imgUrl}/api/blog/image/${category._id}`}
                    alt="No Image Found"
                  />
                  <CardContent>
                    <Typography variant="body2" color="text.secondary">
                      {category.postText}
                    </Typography>
                  </CardContent>
                </Card>
              </>
            );
          })
        )}
        {loadMoreData < category?.length && (
            <Button
              variant="contained"
              color="error"
              sx={{ margin: 2 }}
              onClick={handleMoreData}
            >
              Load More
            </Button>
          )}
      </Container>
    </>
  );
};

export default CategoryDetails;
