import axios from "axios";

// Authentication Details
// Resgister API
const baseAUthUrl = "https://restapinodejs.onrender.com"
export const registerData =async (data)=>{
    try {
        return await axios.post(`${baseAUthUrl}/api/register`, data)
    }
    catch(error){
        console.log('Error while calling api.');
    }
}

//  Login API
export const loginData =async (data)=>{
    try {
        return await axios.post(`${baseAUthUrl}/api/login`, data)
    }
    catch(error){
        console.log('Error while calling api.');
    }
}

// Blog 

const baseBlogUrl = "https://restapinodejs.onrender.com"

