export const allBlogData = async ()=>{
    try{
        return await axios.get(`${baseBlogUrl}/api/allBlog`)
    }catch(error){
        console.log("Error while fetching the api.");
    }
}

// Blog Details

export const blogDetailsData = async ()

// All Category
export const AllCategoryData = async ()=>{
    try{
        return await axios.get(`${baseBlogUrl}/api/showallcategory`)
    }catch(error){
        console.log("Error while fetching api data.");
    }
}

// Recent Post 
export const recentPostData = async ()=>{
    try{
