import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { blogDetailsData, commentsData } from '../service/Api'
import Layout from '../components/Layout'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Container from "@mui/material/Container";

const BlogDetails = () => {
    const [blog, setBlog] = useState([])
    const [comment, setComment] = useState([])
    const [loading, setLoading] = useState(true)

    const imgUrl = "https://restapinodejs.onrender.com";

    const { id } = useParams()
    console.log('id', id);

    const getBlogDetailsData = async ()=>{
        const response = await blogDetailsData(id)
        setBlog(response?.data?.data)
        console.log('data', response);
    }

    const getComments = async ()=>{
        const res_comments = await commentsData(id)
        setComment(res_comments?.data?.post)
        console.log("comments", res_comments);
    }

    console.log('com', comment);

    useEffect(()=>{
        getBlogDetailsData();
        getComments();
    },[])
  return (
    <>
      <Layout title={"Blog-Details"}>
      <Container >
      <Card sx={{boxShadow: "0px 0px 30px rgba(0,0,0,0.5)"}}>
      <CardMedia
        sx={{ height: 600 }}
        image={`${imgUrl}/api/blog/image/${id}`}
        title="green iguana"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {blog.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {blog.postText}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Share</Button>
      </CardActions>
    </Card>
      </Container>
      </Layout>
    </>
  )
}

export default BlogDetails
