import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { categoryDetailsData } from '../service/Api'
import Container from "@mui/material/Container";
import Skeleton from "@mui/material/Skeleton";


const CategoryDetails = () => {
    const [category, setCategory] = useState([])

    const {id} = useParams()

    const getCategoryDetails =async ()=>{
        const response = await categoryDetailsData(id)
        setCategory(response?.data)
        console.log('cat', category);
    }

    useEffect(()=>{
        getCategoryDetails()
    },[])
  return (
    <>
      <Container>
      {loading ? (
                <>
                  <Card
                    sx={{
                      width: 800,
                      boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                    }}
                  >
                    <CardHeader
                      title={
                        <Skeleton variant="text" sx={{ fontSize: "3rem" }} />
                      }
                      subheader={
                        <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                      }
                    />
                    <Skeleton variant="rectangular" width={900} height={400} />
                    <CardContent>
                      <Typography variant="body2" color="text.secondary">
                        {<Skeleton variant="text" sx={{ fontSize: "1rem" }} />}
                      </Typography>
                      <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                    </CardContent>
                  </Card>
                </>
              ) : (
                blog?.length > 0 &&
                blog?.slice(0, loadMoreData).map((blog, key) => {
                  return (
                    <>
                      <Card
                        key={key}
                        sx={{
                          width: 800,
                          boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                        }}
                      >
                        <CardHeader
                          title={blog.title}
                          subheader={blog.createdAt}
                        />
                        <CardMedia
                          component="img"
                          height="400"
                          image={`${imgUrl}/api/blog/image/${blog._id}`}
                          alt="No Image Found"
                        />
                        <CardContent>
                          <Typography variant="body2" color="text.secondary">
                            {blog.postText.slice(0, 300)}
                          </Typography>
                          <Link to={`/blogdetails/${blog._id}`}>
                            Read More
                          </Link>
                        </CardContent>
                      </Card>
                    </>
                  );
                })
              )}
      </Container>
    </>
  )
}

export default CategoryDetails
