import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { categoryDetailsData } from "../service/Api";
import Container from "@mui/material/Container";
import Skeleton from "@mui/material/Skeleton";
import {
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";
import Layout from "../components/Layout";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";

const CategoryDetails = () => {
  const imgUrl = "https://restapinodejs.onrender.com";
  const [category, setCategory] = useState([]);
  const [loading, setLoading] = useState(true);

  const { id } = useParams();

  const navigate = useNavigate();

  const getCategoryDetails = async () => {
    const response = await categoryDetailsData(id);
    setCategory(response?.data?.data);
    setLoading(false);
    console.log("cat", response);
  };

  useEffect(() => {
    getCategoryDetails();
  }, []);

  // Load More Data Option
  const dataPerRow = 2;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  return (
    <>
      <Layout title={"Category-Details"}>
        {!category.length > 0 ? (
          <Modal
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
          >
            <Box sx={style}>
              <Typography id="modal-modal-title" variant="h6" component="h2">
                Text in a modal
              </Typography>
            </Box>
          </Modal>
        ) : (
          <Container>
            {loading ? (
              <>
                <Card
                  sx={{
                    boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                  }}
                >
                  <CardHeader
                    title={
                      <Skeleton variant="text" sx={{ fontSize: "3rem" }} />
                    }
                    subheader={
                      <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                    }
                  />
                  <Skeleton variant="rectangular" height={400} />
                  <CardContent>
                    <Typography variant="body2" color="text.secondary">
                      {<Skeleton variant="text" sx={{ fontSize: "1rem" }} />}
                    </Typography>
                    <Skeleton variant="text" sx={{ fontSize: "1rem" }} />
                  </CardContent>
                </Card>
              </>
            ) : (
              category?.length > 0 &&
              category?.slice(0, loadMoreData).map((category, key) => {
                return (
                  <>
                    <Card
                      key={key}
                      sx={{
                        boxShadow: "0px 0px 30px rgba(0,0,0,0.5)",
                      }}
                    >
                      <CardHeader
                        title={category.title}
                        subheader={category.createdAt}
                      />
                      <CardMedia
                        component="img"
                        height="400"
                        image={`${imgUrl}/api/blog/image/${category._id}`}
                        alt="No Image Found"
                      />
                      <CardContent>
                        <Typography variant="body2" color="text.secondary">
                          {category.postText.slice(0, 300)}
                        </Typography>
                        <Link to={`/blogdetails/${category._id}`}>
                          Read More
                        </Link>
                      </CardContent>
                    </Card>
                  </>
                );
              })
            )}
            {loadMoreData < category?.length && (
              <Button
                variant="contained"
                color="error"
                sx={{ margin: 2 }}
                onClick={handleMoreData}
              >
                Load More
              </Button>
            )}
            <Button
              variant="outlined"
              color="error"
              size="small"
              onClick={() => navigate(-1)}
            >
              Go Back
            </Button>
          </Container>
        )}
      </Layout>
    </>
  );
};

export default CategoryDetails;
