import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { blogDetailsData, commentsData, postComments } from "../service/Api";
import Layout from "../components/Layout";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import TextField from "@mui/material/TextField";
import { toast } from "react-toastify";

const BlogDetails = () => {
  const initialState = {
    name: "",
    email: "",
    comment: "",
  };
  const [blog, setBlog] = useState([]);
  const [comment, setComment] = useState([]);
  const [addComment, setAddComment] = useState(initialState);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  const imgUrl = "https://restapinodejs.onrender.com";

  const { id } = useParams();
  //   console.log("id", id);

  //   Blog Details
  const getBlogDetailsData = async () => {
    const response = await blogDetailsData(id);
    setBlog(response?.data?.data);
    // console.log("data", response);
  };

  //   Fecth Comments
  const getComments = async () => {
    const res_comments = await commentsData(id);
    // console.log("comments", res_comments);
  };

  //   Post Comments
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddComment({ ...addComment, [name]: value });
    console.log(addComment);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response_postComment = await postComments(id);
    if (response_postComment) {
      toast.success(response_postComment?.data?.message);
    } else {
      toast.error("Something Went Wrong");
    }
  };

  console.log("com", comment);

  useEffect(() => {
    getBlogDetailsData();
    getComments();
  }, []);

  const dataPerRow = 2;
  const [loadMoreData, setLoadMoreData] = useState(dataPerRow);
  const handleMoreData = () => {
    setLoadMoreData(loadMoreData + dataPerRow);
  };
  return (
    <>
      <Layout title={"Blog-Details"}>
        <Container>
          <Card sx={{ boxShadow: "0px 0px 30px rgba(0,0,0,0.5)" }}>
            <CardMedia
              sx={{ height: 600 }}
              image={`${imgUrl}/api/blog/image/${id}`}
              title="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {blog.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {blog.postText}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate(-1)}>
                Go Back
              </Button>
            </CardActions>
          </Card>
          {/* Comments */}
          <div>
            <h2>Comments ({comment.length})</h2>
            <Container>
              {comment?.length > 0 &&
                comment.slice(0, loadMoreData)?.map((comments, key) => {
                  return (
                    <>
                      <div style={{ padding: 7 }}>
                        <Typography
                          sx={{
                            fontWeight: "bold",
                            fontFamily: "times new roman",
                            fontSize: 18,
                          }}
                          key={key}
                        >
                          {comments.name}
                        </Typography>
                        <Typography
                          sx={{ fontSize: 12, fontFamily: "times new roman" }}
                          color="text.secondary"
                          gutterBottom
                        >
                          {comments.createdAt}
                        </Typography>
                        <Typography
                          sx={{ fontSize: 18, fontFamily: "times new roman" }}
                          color="text.secondary"
                          gutterBottom
                        >
                          {comments.comment}
                        </Typography>
                      </div>
                    </>
                  );
                })}
              {loadMoreData < comment?.length && (
                <Button
                  variant="outlined"
                  color="error"
                  sx={{ margin: 2 }}
                  onClick={handleMoreData}
                >
                  More Comments
                </Button>
              )}
            </Container>
          </div>
        </Container>
        {/* Post Comments */}
        <Container
          sx={{ boxShadow: "0px 0px 30px rgba(0,0,0,0.5)", height: "auto" }}
        >
          <h2 style={{ paddingTop: 30, paddingBottom: 15 }}>Leave a Comment</h2>
          <div>
            <TextField
              label="Name"
              color="error"
              focused
              sx={{ marginLeft: 5, width: "45%" }}
              value={addComment.name}
              onChange={handleChange}
              required
            />
            <TextField
              label="Email"
              placeholder="Don't worry!! Your mail id will remain private."
              color="error"
              focused
              sx={{ marginLeft: 5, width: "45%" }}
              value={addComment.email}
              onChange={handleChange}
              required
            />
          </div>
          <TextField
            id="outlined-multiline-flexible"
            multiline
            maxRows={5}
            label="Place Your Comments Here"
            color="error"
            focused
            sx={{ marginLeft: 5, marginTop: 5, width: "93%" }}
            value={addComment.comment}
            onChange={handleChange}
            required
          />
          <Button variant="outlined" color="error" sx={{ margin: 5 }}>
            Add Comment
          </Button>
        </Container>
      </Layout>
    </>
  );
};

export default BlogDetails;
